			// 强制初始化方法
			forceInitialize(option) {
				console.log('🔧 强制初始化开始...');
				
				// 设置默认状态
				if (option && option.current) {
					this.current = parseInt(option.current);
					this.l_title = option.l_title || this.getTabName(this.current);
					this.id = option.id;
					console.log('📋 从参数初始化:', { current: this.current, title: this.l_title });
