					this.l_title = this.$t("home.order");
					console.log('📋 使用默认初始化:', { current: this.current, title: this.l_title });
				}
				
				// 延迟初始化组件，确保DOM已准备好
				this.$nextTick(() => {
					setTimeout(() => {
						this.changeInit(this.current);
						console.log('✅ 强制初始化完成');
					}, 100);
				});
			},
			
